window.addEventListener("DOMContentLoaded",()=>{{const e=new IntersectionObserver((t,r)=>{t.forEach(t=>{if(t.isIntersecting){const e=new CountUp(t.target.id,0,t.target.dataset.num,0,t.target.dataset.duration||4,{separator:" ",prefix:t.target.dataset.prefix||"",suffix:t.target.dataset.suffix||""});e.start(()=>{t.target.parentElement.classList.add("numbers__item_done")}),r.unobserve(t.target)}})});return void document.querySelectorAll(".numbers__item span").forEach(t=>{e.observe(t)})}});
